package operator;

public class String_lengthmethod {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
String msg="Java is simple";
System.out.println(msg.length());
System.out.println(msg.toUpperCase());
System.out.println(msg.toLowerCase());
System.out.println(msg.indexOf("is"));
System.out.println(msg.charAt(2));
System.out.println(msg.substring(2));
System.out.println(msg.substring(2,7));
System.out.println(msg.replace("is", "was"));
System.out.println(msg.trim());
System.out.println(msg.indexOf("s"));
System.out.println(msg.concat("JAVA IS"));



System.out.println("My name is \"Devipriya\"");
	}

}
