package operator;

public class assignmentoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2;
		int b=9;
		int c;//declaration
		c=a+b;//definition
		int d=c+10;
		a+=3;
		System.out.println(a);
		b-=7;
		System.out.println(b);
		a%=3;
		System.out.println(a);
		b/=4;
		System.out.println(b);

		a*=9;
		System.out.println(a);

		c*=5;
		System.out.println(c);

		c%=3;
		System.out.println(c);
	}

}
